export const completionShTemplate = `###-begin-***REMOVED******REMOVED***app_name***REMOVED******REMOVED***-completions-###
#
# yargs command completion script
#
# Installation: ***REMOVED******REMOVED***app_path***REMOVED******REMOVED*** ***REMOVED******REMOVED***completion_command***REMOVED******REMOVED*** >> ~/.bashrc
#    or ***REMOVED******REMOVED***app_path***REMOVED******REMOVED*** ***REMOVED******REMOVED***completion_command***REMOVED******REMOVED*** >> ~/.bash_profile on OSX.
#
_***REMOVED******REMOVED***app_name***REMOVED******REMOVED***_yargs_completions()
***REMOVED***
    local cur_word args type_list

    cur_word="\$***REMOVED***COMP_WORDS[COMP_CWORD]***REMOVED***"
    args=("\$***REMOVED***COMP_WORDS[@]***REMOVED***")

    # ask yargs to generate completions.
    type_list=$(***REMOVED******REMOVED***app_path***REMOVED******REMOVED*** --get-yargs-completions "\$***REMOVED***args[@]***REMOVED***")

    COMPREPLY=( $(compgen -W "\$***REMOVED***type_list***REMOVED***" -- \$***REMOVED***cur_word***REMOVED***) )

    # if no match was found, fall back to filename completion
    if [ \$***REMOVED***#COMPREPLY[@]***REMOVED*** -eq 0 ]; then
      COMPREPLY=()
    fi

    return 0
***REMOVED***
complete -o bashdefault -o default -F _***REMOVED******REMOVED***app_name***REMOVED******REMOVED***_yargs_completions ***REMOVED******REMOVED***app_name***REMOVED******REMOVED***
###-end-***REMOVED******REMOVED***app_name***REMOVED******REMOVED***-completions-###
`;
export const completionZshTemplate = `#compdef ***REMOVED******REMOVED***app_name***REMOVED******REMOVED***
###-begin-***REMOVED******REMOVED***app_name***REMOVED******REMOVED***-completions-###
#
# yargs command completion script
#
# Installation: ***REMOVED******REMOVED***app_path***REMOVED******REMOVED*** ***REMOVED******REMOVED***completion_command***REMOVED******REMOVED*** >> ~/.zshrc
#    or ***REMOVED******REMOVED***app_path***REMOVED******REMOVED*** ***REMOVED******REMOVED***completion_command***REMOVED******REMOVED*** >> ~/.zprofile on OSX.
#
_***REMOVED******REMOVED***app_name***REMOVED******REMOVED***_yargs_completions()
***REMOVED***
  local reply
  local si=$IFS
  IFS=$'\n' reply=($(COMP_CWORD="$((CURRENT-1))" COMP_LINE="$BUFFER" COMP_POINT="$CURSOR" ***REMOVED******REMOVED***app_path***REMOVED******REMOVED*** --get-yargs-completions "\$***REMOVED***words[@]***REMOVED***"))
  IFS=$si
  _describe 'values' reply
***REMOVED***
compdef _***REMOVED******REMOVED***app_name***REMOVED******REMOVED***_yargs_completions ***REMOVED******REMOVED***app_name***REMOVED******REMOVED***
###-end-***REMOVED******REMOVED***app_name***REMOVED******REMOVED***-completions-###
`;
